	 ( SELECT @RNUM := 0 ) b) c where c.rnum >= #{start} and c.rnum <= #{end}

mapper.update:
update album_reply as a, (select max(replynumber) maxi from album_reply) r
set a.replygroup=a.replynumber 
where a.replynumber=r.maxi;
