mapper.select:
SELECT c.* 
FROM (SELECT @RNUM := @RNUM + 1 AS rnum, a.* FROM (SELECT * FROM artist_reply where artistnumber=#{artistNumber} ORDER BY replygroup DESC) a,  
	 ( SELECT @RNUM := 0 ) b) c where c.rnum >= #{start} and c.rnum <= #{end}

mapper.update:
update artist_reply as a, (select max(replynumber) maxi from artist_reply) r
set a.replygroup=a.replynumber 
where a.replynumber=r.maxi;
